
-- the 3 types stored as enums.
CREATE TYPE fuel_types AS ENUM ( 'gasoline', 'mild hybrid', 'pure electric');


-- table scheme for vehicles.
BEGIN;
DROP TABLE IF EXISTS vehicles;
CREATE TABLE vehicles (
  license_plate VARCHAR(8) PRIMARY KEY UNIQUE NOT NULL,
  capacity INT NOT NULL,
  range FLOAT NOT NULL,
  fuel fuel_types,
  brand VARCHAR(255),
  model VARCHAR(255)
);
COMMIT;


